package week1.day2;

public class LearnIfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//if condition
		int a = 9;
		int b = 8;
		
		if (a<b)
		{
		System.out.println("a is less than b");
	}
		
		//else condition
		else
		{
			System.out.println("a is greater than b");
		}

		
		System.out.println("---------------------"); 
		
		
		
		
}
	
}
